<?phperror_reporting(E_ALL);//error_reporting(E_ERROR);Class PopUp {	// Contiene el codigo HTML que se generara para el PopUp	var $HtmlCode="";	//Ancho del Pop-Up	var $Width=100;	// Nombre del Pop-up para el formulario	var $Name="popup1";	// Codigo de string inicial. NA=No Aplica	var $IniCode="NA";	// String inicial del pop up. Si no tiene se asume que no debe existir	var $IniString="";	// Indica que elemento del pop-up sera seleccionado inicialmente	var $ItemSelected=0;	// Arreglo de codigos y descripciones del popup	var $Codes;	var $Desc;	var $OptSelect="";	function PopUp($Codigos, $Descripciones, $Nombre, $CodIni, $DescIni, $Item, $Ancho, $OpcionesSelect) {		$this->Codes=$Codigos;		$this->Desc=$Descripciones;		$this->Name=$Nombre;		$this->IniCode=$CodIni;		$this->IniString=$DescIni;		$this->ItemSelected=$Item;		$this->Width=$Ancho;		$this->OptSelect=$OpcionesSelect;		$this->HtmlCode="";		$this->HtmlCode=$this->HtmlCode="<select name='$Nombre'  $OpcionesSelect >\n\n";		// Si existe el string inicial quiere decir que el pop-up debe llevar		// una explicacion antes de la lista de elementos y debe estar seleccionada		$selected=0;		if ($DescIni != "") { //}&& ($CodIni != "")) {			$this->HtmlCode=$this->HtmlCode."<option selected value='$CodIni'>$DescIni ... </option>\n";			$selected=1;		}		// Ahora recorrer todos los elementos del arreglo y armar el POp-Up		$i=0;		$limite=sizeof($Codigos);		if ($limite>200) {			$limite=200;		}               		for ($i = 0; $i < $limite; $i++) {			// Se debe colocar el elemento escogido en la lista si se requiere			// teniendo en cuenta que no se envio un texto por defecto ya seleccionado			if ($Codigos[$i]===$Item) {//001 == 1                                				//if	($selected==0) {					$code="<option selected value='{$Codigos[$i]}'>{$Descripciones[$i]}</option>\n";					$this->HtmlCode=$this->HtmlCode.$code;			}			else {					$code="<option value='{$Codigos[$i]}'>{$Descripciones[$i]}</option>\n";					$this->HtmlCode=$this->HtmlCode.$code;			} /*			} else {				$code="<Option Value='".$Codigos[$i]."'>".$Descripciones[$i]."</option>\n";				$this->HtmlCode=$this->HtmlCode.$code;			} */		}		$this->HtmlCode=$this->HtmlCode."\n</select>\n";	}	function  echoHtml() {		print $this->HtmlCode;	}    function  getHtml() {		return $this->HtmlCode;	}	//	function  echoHtmlMovil() {			$ccJqmovil=	"<div class='ui-select'><div data-corners='true' data-shadow='true' data-iconshadow='true' data-wrapperels='span' data-icon='arrow-d' data-iconpos='right' data-theme='c' class='ui-btn ui-shadow ui-btn-corner-all ui-btn-icon-right ui-btn-up-c'><span class='ui-btn-inner ui-btn-corner-all'><span class='ui-btn-text'><span>".$this->Name."</span></span><span class='ui-icon ui-icon-arrow-d ui-icon-shadow'>&nbsp;</span></span>";		//		print $ccJqmovil.$this->HtmlCode;		return $this->HtmlCode;		//		print "<label for='detalleRespBact' class='select'>".$this->Name.":</label>".$this->HtmlCode;	}}?>